  background-color: #f5f5f5;
  height: 80vh;
  border-radius: 180px;
  border-top-right-radius: 0px;
  border-bottom-left-radius: 0px;
  border: 2px solid red;
  padding-top: 7rem;
  .tutorContents {
    display: grid;
    margin-left: 10rem;
    width: 80%;
    .tutorDetails {
      background-color: white;
      padding-left: 1.5rem;
      border-radius: 10px;
      width: 250px;
      height: 260px;
      h5 {
        color: red;
        border-radius: 120px;
        padding: 0.5rem 0.98rem;
        background-color: pink;
        width: fit-content;
      }

      .workDetails {
        display: flex;
        div {
          display: flex;
          flex-direction: row;
        }
        div:nth-child(2) {
          margin-left: 3rem;
        }

        img {
          opacity: 0.68;
          fill: gray;
          height: 13px;
        }
        p {
          margin-top: -0.124rem;
          margin-left: 0.34rem;
          font-size: 0.762rem;
        }
      }

      .photo {
        display: flex;
        margin-top: 1.2rem;
        width: fit-content;
        img {
          border-radius: 3.5px;
          background: #7fa99b;

          width: 40px;
          height: 45px;
        }
        h6 {
          font-size: 0.98rem;
          margin-left: 1rem;
          position: absolute;
          left: 15rem;
          top: 152.9rem;
