import React from "react";
import styled from "styled-components";
import Header from "./Header";
import SearchIcon from "../Images/search.svg";
// css
const BannerContainer = styled.div`
  background: #3c415e;
  height: 100vh;
`;

function Banner() {
  return (
    <BannerContainer>
      <Header />
      <div className="bannerContents">
        <div className="welcomeText">
          <h3>We help you find and manage tutors.</h3>
          <p>With Gurules you can find and manage your meetings with tutors</p>
          <div className="search">
            <img src={SearchIcon} />
            <input type="text" placeholder="search subject, tutor's name or location" />
            <button className="orangeBtn">Search</button>
          </div>
        </div>
      </div>
    </BannerContainer>
  );
}

export default Banner;
