import React from "react";
import styled from "styled-components";
import Header from "./Header";
import SearchIcon from "../Images/search.svg";
import Image from "../Images/bannerimg.png";
// import Image from "../Images/bannerImage.png";

// css
const BannerContainer = styled.div`
  background: #3c415e;
  height: 100vh;

  .bannerContents {
    display: flex;
    margin: auto 0;
    margin: auto 0;
    padding: 3rem 10rem;
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    color: #f7f4e3;
  }
  .welcomeText h3 {
    font-size: 2.6rem;
    line-height: 2rem;
  }
`;

function Banner() {
  return (
    <BannerContainer>
      <Header />
      <div className="bannerContents">
        <div className="welcomeText">
          <h3>
            We help you find <br />
            and manage tutors.
          </h3>
          <p>
            With Gurules you can find and manage <br /> your meetings with tutors
          </p>
          <div className="search">
            <img src={SearchIcon} alt="search for tutor" />
            <input type="text" placeholder="search subject, tutor's name or location" />
            <button className="orangeBtn">Search</button>
          </div>
        </div>

        <div>
          <img src={Image} alt="student" />
        </div>
      </div>
    </BannerContainer>
  );
}

export default Banner;
