import React from "react";
import styled from "styled-components";
import Header from "./Header";
import SearchIcon from "../Images/search.svg";
import tutor1 from "../Images/bannerimg.png";
import Image from "../Images/bannerImage.png";

// css
const BannerContainer = styled.div`
  background: #3c415e;
  height: 100vh;

  .bannerContents {
    display: flex;
    margin: auto 0;
    margin: auto 0;
    padding: 3rem 10rem;
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    color: #f7f4e3;
  }
  .welcomeText h3 {
    font-size: 4.25rem;
    line-height: 5.36rem;
    letter-spacing: 0.11rem;
  }
  .welcomeText p {
    margin-top: -3rem;
    font-size: 1.17rem;
    letter-spacing: 0.14rem;
    line-height: 2.16rem;

    opacity: 0.68;
  }
  .search {
    position: absolute;

    z-index: 3000000;
    border-radius: 5px;
    padding: 0.7rem;
    background: white;
    display: flex;
    width: 50%;
    flex-direction: row;
    justify-content: space-between;
  }
  .search img {
    font-size: 0.1rem;
    height: 14px;
  }
  .search input {
    background: transparent;
    border: none;
    color: black;
  }
  .orangeBtn {
    background: #ff7657;
    padding: 0.99rem 1.86rem !important;
    border-radius: 5px;
    border: none;
    color: #f7f4e3;
    margin-left: 3rem;
    font-weight: bold;
    font-size: 1.12rem;
  }
  .bannerImage {
    background-color: #fee4a6;
    ${"" /* position: absolute; */}
    ${"" /* right: 140px; */}
    z-index: 30;
    width: 41%;
    height: 790px;
    border-top-right-radius: 200px;
    border-bottom-left-radius: 200px;
  }
  .student {
    width: 100%;
  }

  .chats {
    position: absolute;
    right: 45px;
    top: 140px;
  }
  .tutorChat {
    display: flex;
    flex-direction: row;
    border-radius: 70px;
    border-top-left-radius: 0px;
    border-bottom-left-radius: 120px;

    ${"" /* padding: */}
    padding:0 .4rem .24rem .3rem;
    background: #ff7657;
    width: 270px;
  }
  .reply {
    border-radius: 70px;
    border-top-right-radius: 0px;
    border-bottom-right-radius: 120px;
    background: #ff7657;
    ${"" /* padding:0 4rem .24rem .3rem; */}
    padding:.97rem 0rem .95rem 1.8rem;
    line-height: 1.35rem;
    font-size: 0.89rem;
    font-weight: 600;
    width: 220px;
    margin-left: 8.3rem;
  }
  .tutorChat p {
    line-height: 1.35rem;
    font-size: 0.89rem;
    font-weight: 600;
  }

  .tutorImage {
    width: 45px;
    height: 45px;
    border-radius: 50%;
    background: #3c415e;
    margin-left: 1.3rem;
    margin-right: 1rem;
    margin-top: 1rem;
  }
  .friendsConvo {
    display: flex;
    flex-direction: column;
  }

  .friendsConvo img {
    border-radius: 50%;
    border-bottom-right-radius: 0px;

    width: 50px;
    height: 40px;
    padding: 1rem;
    background: #ff7657;
  }
`;

function Banner() {
  return (
    <BannerContainer>
      <Header />
      <div className="bannerContents">
        <div className="welcomeText">
          <h3>
            We help you find <br />
            and manage tutors.
          </h3>
          <p>
            With Gurules you can find and manage <br /> your meetings with tutors.
          </p>
          <div className="search">
            <img src={SearchIcon} alt="search for tutor" />
            <input type="text" placeholder="search subject, tutor's name or location" />
            <button className="orangeBtn">Search</button>
          </div>
        </div>

        <div className="bannerImage">
          <div className="chats">
            <div className="tutorChat">
              <img className="tutorImage" src={tutor1} />
              <p>
                Can we change the <br></br>for schedule?{" "}
              </p>
            </div>
            <p className="reply">
              Of course, how about <br />
              tommorow?{" "}
            </p>
          </div>
          <div className="friends">
            <div className="friendsConvo">
              <img className="frinedOne" src={tutor1} />
              <img className="friendTwo" src={tutor1} />
              <img className="friendThree" src={tutor1} />
            </div>
            <img src={Image} alt="student" className="student" />
          </div>
        </div>
      </div>
    </BannerContainer>
  );
}

export default Banner;
