import React from "react";
import styled from "styled-components";
import image from "../Images/friend1.png";
import location from "../Images/location.svg";
import time from "../Images/time.svg";

// css
const TutorsContainer = styled.div`
  background-color: #f5f5f5;
  height: 80vh;
  border-radius: 180px;
  border-top-right-radius: 0px;
  border-bottom-left-radius: 0px;
  border: 2px solid red;
  padding-top: 7rem;
  .tutorContents {
    display: grid;
    margin-left: 10rem;
    width: 80%;
    .tutorDetails {
      background-color: white;
      padding-left: 1.5rem;
      border-radius: 20px;
      width: 300px;
      height: 300px;
      h5 {
        color: red;
        border-radius: 30%;
        padding: 0.5rem;
        background-color: tomato;
        width: fit-content;
      }

      .workDetails {
        display: flex;
        div {
          display: flex;
          flex-direction: row;
        }
      }

      .photo {
        display: flex;
        flex-direction: row;
        img {
          border-radius: 3.5px;
          background: #7fa99b;

          width: 40px;
          height: 45px;
        }
      }
    }
  }
`;

function Tutors() {
  return (
    <TutorsContainer>
      <div className="tutorContents">
        <div className="tutorDetails">
          <h5>Designs</h5>
          <h3>Graphic Design Tutor (Photoshop & Illustrator)</h3>
          <div className="workDetails">
            <div className="location">
              <img src={location} alt="locatio" />
              <p>Tanzania</p>
            </div>
            <div className="time">
              <img src={time} alt="experience" />
              <p>3 years+</p>
            </div>
          </div>
          <div className="photo">
            <img src={image} alt="tutor" />
            <h6>Sarah Ally</h6>
          </div>
        </div>
      </div>
    </TutorsContainer>
  );
}

export default Tutors;
