import React from "react";
import styled from "styled-components";
import image from "../Images/friend1.png";
import location from "../Images/location.svg";
import time from "../Images/time.svg";
import music from "../Images/playing.jpeg";
import music1 from "../Images/playin2.jpeg";

// css
const TutorsContainer = styled.div`
  background-color: #f5f5f5;
  height: 100vh;
  border-radius: 180px;
  border-top-right-radius: 0px;
  border-bottom-left-radius: 0px;
  border: 2px solid red;
  padding-top: 7rem;
  .tutorContents {
    display: grid;
    grid-columns: 3;
    grid-rows: 2;
    grid-template-columns: repeat(3, 1fr);
    grid-template-rows: repeat(2, 1fr);
    grid-gap: 10px;
    margin-left: 10rem;
    width: 60%;
    .tutorDetails {
      background-color: white;
      padding-left: 1.5rem;
      border-radius: 10px;
      width: 250px;
      height: 260px;
      h5 {
        color: red;
        border-radius: 120px;
        padding: 0.5rem 0.98rem;
        background-color: pink;
        width: fit-content;
      }

      .workDetails {
        display: flex;
        div {
          display: flex;
          flex-direction: row;
        }
        div:nth-child(2) {
          margin-left: 3rem;
        }

        img {
          opacity: 0.68;
          fill: gray;
          height: 13px;
        }
        p {
          margin-top: -0.124rem;
          margin-left: 0.34rem;
          font-size: 0.762rem;
        }
      }

      .photo {
        display: flex;
        ${"" /* margin-top: 1.2rem; */}
        width: fit-content;
        img {
          border-radius: 3.5px;
          background: #7fa99b;
          margin-top: 1.2rem;
          width: 40px;
          height: 45px;
        }
        h6 {
          font-size: 0.98rem;
          margin-left: 1rem;
          ${"" /* position: absolute; */}
          left: 15rem;
          top: 152.9rem;
        }
      }
    }

    .tutorDetails:nth-child(2) {
      height: 450px;
      width: 300px;
      padding-right: 1rem;
      h5 {
        color: yellow;
        background-color: gray;
      }
      p {
        opacity: 0.78;
        line-height: 2rem;
      }
      .photo {
        margin-top: 2.5rem;
      }

      .workDetails {
        p {
          margin-top: -0.63rem;
        }
      }
    }

    .tutorDetails:nth-child(3) {
      background-color: transparent;
      margin-top: -7rem;
      margin-left: 2rem;

      h3 {
        font-size: 4rem;
        line-height: 5.36rem;
        color: #3c415e;
        width: 27rem;
        letter-spacing: 0.11rem;
      }
      a {
        color: #3c415e;
        cursor: pointer;
        border-bottom: 1px solid #3c415e;
      }
    }

    .tutorDetails:nth-child(4) {
      color: white;
      background-color: transparent;
      background-repeat: no-repeat;
      background-size: cover;
      background-position: center;
      background-image: url(${music});

      h5 {
        color: #3c415e;
        background-color: gray;
      }
    }
  }
`;

function Tutors() {
  return (
    <TutorsContainer>
      <div className="tutorContents">
        <div className="tutorDetails">
          <h5>Designs</h5>
          <h3>Graphic Design Tutor (Photoshop & Illustrator)</h3>
          <div className="workDetails">
            <div className="location">
              <img src={location} alt="locatio" />
              <p>Tanzania</p>
            </div>
            <div className="time">
              <img src={time} alt="experience" />
              <p>3 years+</p>
            </div>
          </div>
          <div className="photo">
            <img src={image} alt="tutor" />
            <h6>Sarah Ally</h6>
          </div>
        </div>
        {/* detail two */}

        <div className="tutorDetails">
          <h5>Language</h5>
          <h3>
            Specialist English
            <br /> Tutoring
          </h3>
          <div className="workDetails">
            <div className="location">
              <img src={location} alt="locatio" />
              <p>Kenya</p>
            </div>
            <div className="time">
              <img src={time} alt="experience" />
              <p>10 years+</p>
            </div>
          </div>
          <p>Hey there. I'm Rafy a tutor with over 10 yers of experiences who has worked with over 1000 students</p>
          <div className="photo">
            <img src={image} alt="tutor" />
            <h6>Rafia Ramadhani</h6>
          </div>
        </div>
        {/* detail three */}
        <div className="tutorDetails">
          <h3>The most popular tutor</h3>

          <a>Explore All 15,000+ Tutors</a>
        </div>

        {/* detail four */}
        <div className="tutorDetails">
          <h5>Designs</h5>
          <h3>
            Piano and Violin <br />
            Tutor
          </h3>
          <div className="workDetails">
            <div className="location">
              <img src={location} alt="locatio" />
              <p>Tanzania</p>
            </div>
            <div className="time">
              <img src={time} alt="experience" />
              <p>3 years+</p>
            </div>
